printf "Register/Login: " ; read -k 1 operation ;echo;if [ "$operation" = "r" ];then; printf "Please enter first username: " ; read username ; printf "Please enter first password: " ; read -s password ; password=$(md5 -q -s "$password"); output="$username"'\n'"$password" ; echo "$output" > saved; else; printf "Please enter username: " ; read username ;printf "Please enter password: " ; read -s password ; password=$(md5 -q -s "$password"); read -r usermatch < saved; passmatch=$(awk 'NR==2' saved); echo; if [ "$username" = "$usermatch" ] && [ "$password" = "$passmatch" ]; then  echo "Login succesful"; else echo "Incorrect password"; fi ; fi ;
